function playMusic(){player.play(),intv=setInterval(update,100),getFullTime()}function pauseMusic(){player.pause(),clearInterval(intv)}function stopMusic(){player.pause(),player.currentTime=0,clearInterval(intv)}function update(){document.getElementById("songTime").innerHTML=millisToMins(player.currentTime)}function getFullTime(){document.getElementById("songTimeFull").innerHTML=millisToMins(player.duration)}function millisToMins(e){var n=Math.floor(e%31536e3%86400%3600/60),t=e%31536e3%86400%3600%60;return t>=10?n+":"+Math.round(t):n+":0"+Math.round(t)}var player,intv,slider;window.onload=function(){document.getElementById("btnPlay").addEventListener("click",playMusic,!1),document.getElementById("btnPause").addEventListener("click",pauseMusic,!1),document.getElementById("btnStop").addEventListener("click",stopMusic,!1),player=document.getElementById("player")};